import { receiveUser } from '../actions/userActions';
import { createUser, login, logout, getUser, getCalendars } from '../util/userUtil';

const UserMiddleware = ({getState, dispatch}) => next => action => {
  switch (action.type) {
    case 'CREATE_USER':
      createUser(action.payload.user,
                 (user) => dispatch(receiveUser(user)));
      break;
    case 'LOGIN':
      login(action.payload.creds,
            (user) => dispatch(receiveUser(user)));
      break;
    case 'LOGOUT':
      logout(action.payload.id,
             (user) => dispatch(loggedOutUser(user)));
      break;
    case 'GET_USER':
      getUser(action.payload.id,
              (user) => dispatch(receiveUser(user)));
      break;
  }
}
